import React, { useContext } from "react";
import { GlobalContext } from "../context/GlobalState";

export const Transaction = ({ transaction }) => {
    const sign = transaction.amount < 0 ? "-" : "+";
    const { deleteTransaction } = useContext(GlobalContext);
    return (
        <>
            <li style={transaction.amount < 0 ? styles.minus : styles.plus} onClick={() => deleteTransaction(transaction.id)}>
                {transaction.text}
                <span>{sign}{Math.abs(transaction.amount)}$</span>

            </li>
            <button type="button" >X</button>
        </>
    );
};
const styles = {
    minus: {
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center",
        marginBottom: "20px",
        padding: "10px",
        borderRadius: "10px",
        width: "40vw",
        backgroundColor: "#fe879a"
    },
    plus: {
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center",
        marginBottom: "20px",
        padding: "10px",
        borderRadius: "10px",
        width: "40vw",
        backgroundColor: "#4cd964",
    }
};
